  // };

  const getUserInput = event => {
    setSearch(event.target.value);
    console.log(search);

    updateSearchTable();
    //ReactDOM.render(testData, document.getElementById('root'));
  };

  const updateSearchTable = () => {
    let updateData = [];

    for (var index = 0; index < data.length; index++) {

      if(data[index].name.includes(search)){
        updateData.push(result.data[index]);
      }
      else if(data[index].address.includes(search)){
        updateData.push(result.data[index]);
      }
     }

     setData(updateData);
  };


  return (
    <div>
      <img src={drugstorepic} className={"subpage_img"} alt="drugstore"/>

      <form action={getURL()} method="get">
        <input type="text" name="input" value={searchCity} onChange={getUserInput} placeholder="Search"></input>
